import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import api from "../../../api/apiClient";
import { useParams } from "react-router-dom";
import swal from "sweetalert";
import { Button } from "@material-ui/core";

const ResetPassForm = () => {
  const [old, setOld] = useState("");
  const [newPass, setNewPass] = useState("");
  const [cnfrm, setCnfrm] = useState("");
  const params = useParams();
  const changePass = async (req, res) => {
    if (cnfrm !== newPass) {
      swal({
        title: "Password do not match",
        icon: "error",
        buttons: true,
        closeOnClickOutside: true,
        closeOnEsc: true,
      });
    } else {
      const token = params.token;
      const data = {
        token: token,
        newPassword: newPass,
        cnfrmPass: cnfrm,
      };
      const msg = await api.post("/forgot", data);
      const message = msg.data.message;
      console.log(message);
      if (message === "Token is expired") {
        const res = await swal({
          title: message,
          icon: "error",
          buttons: true,
          closeOnClickOutside: true,
          closeOnEsc: true,
        });
        if (res) {
          window.location.href = "https://teamdcoder.com/";
        }
      } else if (message === "Invalid Password") {
        swal({
          title: message,
          icon: "error",
          buttons: true,
          closeOnClickOutside: true,
          closeOnEsc: true,
        });
      } else {
        const res = await swal({
          title: "Password Changed Succesfully",
          icon: "success",
          buttons: true,
          closeOnClickOutside: true,
          closeOnEsc: true,
        });
        if (res) {
          window.location.href = "https://teamdcoder.com/";
        }
      }
    }
  };
  const myStyle = {
    display: "flex",
    height: "100vh",
    alignItems: "center",
    justifyContent: "center",
  };
  return (
    <div style={myStyle}>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "white",
          padding: "50px",
          borderRadius: "10px",
        }}
        onSubmit={(e) => e.preventDefault()}
      >
        <label>New Password</label>
        <TextField
          value={newPass}
          onChange={(e) => setNewPass(e.target.value)}
          style={{ marginBottom: "10px" }}
          type="password"
          required
          error={newPass.length < 8 && newPass.length !== 0}
          helperText={
            newPass.length < 8 &&
            newPass.length !== 0 &&
            "Password must be greater than 8 characters."
          }
        />
        <label>Confirm New Password</label>
        <TextField
          value={cnfrm}
          onChange={(e) => setCnfrm(e.target.value)}
          style={{ marginBottom: "10px" }}
          required
          type="password"
          error={cnfrm.length < 8 && cnfrm.length !== 0}
          helperText={
            cnfrm.length < 8 &&
            cnfrm.length !== 0 &&
            "Password must be greater than 8 characters."
          }
        />
        <Button
          type="submit"
          onClick={changePass}
          disabled={newPass.length < 8 || cnfrm.length < 8}
          variant="contained"
          color="primary"
        >
          Submit
        </Button>
      </form>
    </div>
  );
};

export default ResetPassForm;
