import tw from "twin.macro";

export const PrimaryLink = tw.a`cursor-pointer font-bold text-primary-500 border-b-2 border-transparent hocus:border-primary-500 hocus:text-primary-800 transition duration-300`;
export const TealLink = tw.a`cursor-pointer font-bold text-teal-500 border-b-2 border-transparent hocus:border-teal-500 hocus:text-teal-800 transition duration-300`;
export const BlueLink = tw.a`cursor-pointer font-bold text-blue-600 border-b-2 border-transparent hocus:border-blue-800 hocus:text-blue-300 transition duration-300`;
