import { Button } from "@material-ui/core";
function Pagination({ postsPerPage, totalPosts, paginate }) {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i += 1) {
    pageNumbers.push(i);
  }

  return (
    <div
      style={{
        display: "flex",
        gap: "10px",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {pageNumbers.map((num) => (
        <Button
          variant="contained"
          color="primary"
          onClick={() => paginate(num)}
        >
          {num}
        </Button>
      ))}
